import com.mapbox.geojson.Point
import com.mapbox.maps.CameraOptions
import com.mapbox.maps.MapView
import com.mapbox.maps.Style
import com.mapbox.maps.plugin.annotation.annotations
import com.mapbox.maps.plugin.annotation.generated.PointAnnotationOptions
import com.mapbox.maps.plugin.annotation.generated.createPointAnnotationManager
import kotlinx.coroutines.flow.collectLatest
import kotlinx.coroutines.launch

class MapFragment : Fragment() {

 private var _binding: FragmentMapBinding? = null
 private val binding get() = _binding!!

 private val positioningViewModel: PositioningViewModel by activityViewModels()

 private lateinit var mapView: MapView
 private var userLocationMarker: com.mapbox.maps.plugin.annotation.generated.PointAnnotation? = null

 override fun onCreateView(
 inflater: LayoutInflater,
 container: ViewGroup?,
 savedInstanceState: Bundle?
 ): View {
 _binding = FragmentMapBinding.inflate(inflater, container, false)
 return binding.root
 }

 override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
 super.onViewCreated(view, savedInstanceState)

 // Initialize map
 mapView = binding.mapView

 // Setup map style and initial position
 setupMap()

 // Observe user position updates
 observePositionUpdates()

 // Setup floor controls
 setupFloorControls()
 }

 private fun setupMap() {
 // Load map style
 mapView.getMapboxMap().loadStyleUri(Style.MAPBOX_STREETS) {
 // Add custom indoor map layer here
 addIndoorMapLayer(positioningViewModel.currentFloor.value)
 }

 // Set initial camera position (centered on the building)
 val initialCameraPosition = CameraOptions.Builder()
 .center(Point.fromLngLat(0.0, 0.0)) // Default position, replace with actual coordinates
 .zoom(18.0) // High zoom level for indoor mapping
 .build()

 mapView.getMapboxMap().setCamera(initialCameraPosition)
 }

 private fun addIndoorMapLayer(floor: Int) {
 // In a real app, this would load the floor plan for the specific floor
 // from Firebase or local assets

 // For now, we'll use a placeholder implementation
 val mapboxMap = mapView.getMapboxMap()

 // Add floor-specific layers here
 // For example:
 // mapboxMap.getStyle()?.addSource(...)
 // mapboxMap.getStyle()?.addLayer(...)
 }

 private fun observePositionUpdates() {
 viewLifecycleOwner.lifecycleScope.launch {
 positioningViewModel.currentPosition.collectLatest { position ->
 position?.let {
 updateUserLocationOnMap(it)
 }
 }
 }

 // Observe floor changes
 viewLifecycleOwner.lifecycleScope.launch {
 positioningViewModel.currentFloor.collectLatest { floor ->
 // Update floor indicator
 binding.floorText.text = getString(R.string.floor_indicator, floor)

 // Update map layer to show correct floor
 addIndoorMapLayer(floor)
 }
 }
 }

 private fun updateUserLocationOnMap(position: Position) {
 // Convert app coordinates to map coordinates
 // This conversion will depend on how your floor plan is georeferenced
 val point = Point.fromLngLat(
 position.x / 1000.0, // Example conversion, adjust based on your coordinate system
 position.y / 1000.0 // Example conversion, adjust based on your coordinate system
 )

 // Create or update user location marker
 val annotationApi = mapView.annotations
 val pointAnnotationManager = annotationApi.createPointAnnotationManager()

 if (userLocationMarker == null) {
 // Create new marker
 val pointAnnotationOptions = PointAnnotationOptions()
 .withPoint(point)
 .withIconImage(requireContext().getDrawable(R.drawable.ic_location_marker)!!)

 userLocationMarker = pointAnnotationManager.create(pointAnnotationOptions)
 } else {
 // Update existing marker
 userLocationMarker?.let {
 it.point = point
 pointAnnotationManager.update(it)
 }
 }

 // Center map on user if tracking is enabled
 if (binding.trackUserSwitch.isChecked) {
 val cameraPosition = CameraOptions.Builder()
 .center(point)
 .zoom(18.0)
 .build()

 mapView.getMapboxMap().setCamera(cameraPosition)
 }
 }

 private fun setupFloorControls() {
 // Floor up button
 binding.floorUpButton.setOnClickListener {
 val currentFloor = positioningViewModel.currentFloor.value
 positioningViewModel.setCurrentFloor(currentFloor + 1)
 }

 // Floor down button
 binding.floorDownButton.setOnClickListener {
 val currentFloor = positioningViewModel.currentFloor.value
 if (currentFloor > 1) {
 positioningViewModel.setCurrentFloor(currentFloor - 1)
 }
 }
 }

 override fun onDestroyView() {
 super.onDestroyView()
 _binding = null
 }
}
