import com.mapbox.maps.plugin.annotation.generated.PointAnnotationOptions
import com.mapbox.maps.plugin.annotation.generated.PolylineAnnotationOptions
import com.mapbox.maps.plugin.annotation.generated.createPointAnnotationManager
import com.mapbox.maps.plugin.annotation.generated.createPolylineAnnotationManager
import kotlinx.coroutines.flow.collectLatest
import kotlinx.coroutines.launch

class PathfindingFragment : Fragment() {

 private var _binding: FragmentPathfindingBinding? = null
 private val binding get() = _binding!!

 private val positioningViewModel: PositioningViewModel by activityViewModels()
 private val args: PathfindingFragmentArgs by navArgs()

 private lateinit var startPosition: Position
 private lateinit var endPosition: Position

 override fun onCreateView(
 inflater: LayoutInflater,
 container: ViewGroup?,
 savedInstanceState: Bundle?
 ): View {
 _binding = FragmentPathfindingBinding.inflate(inflater, container, false)
 return binding.root
 }

 override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
 super.onViewCreated(view, savedInstanceState)

 // Get positions from arguments
 startPosition = args.startPosition
 endPosition = args.endPosition

 // Initialize map
 setupMap()

 // Calculate and display path
 calculatePath()

 // Observe position updates
 observePositionUpdates()
 }

 private fun setupMap() {
 // Load map style
 binding.mapView.getMapboxMap().loadStyleUri(Style.MAPBOX_STREETS) {
 // Add custom indoor map layer here for the current floor
 addIndoorMapLayer(startPosition.floor)
 }

 // Set initial camera position to see the path
 val midpoint = Position(
 (startPosition.x + endPosition.x) / 2,
 (startPosition.y + endPosition.y) / 2,
 startPosition.floor
 )

 val initialCameraPosition = CameraOptions.Builder()
 .center(convertPositionToPoint(midpoint))
 .zoom(18.0) // High zoom level for indoor mapping
 .build()

 binding.mapView.getMapboxMap().setCamera(initialCameraPosition)
 }

 private fun addIndoorMapLayer(floor: Int) {
 // In a real app, this would load the floor plan for the specific floor
 // from Firebase or local assets
 }

 private fun calculatePath() {
 // Get navigation nodes from ViewModel
 val navigationNodes = positioningViewModel.getNavigationNodes()

 // Create pathfinding engine
 val pathfindingEngine = PathfindingEngine(navigationNodes)

 // Calculate path
 val path = pathfindingEngine.findPath(startPosition, endPosition)

 path?.let {
 displayPath(it)
 displayPathInfo(it)
 } ?: run {
 // Show error if path not found
 binding.pathInfoText.text = "No path found between locations"
 }
 }

 private fun displayPath(path: Path) {
 // Convert path positions to points
 val points = path.waypoints.map { convertPositionToPoint(it) }

 // Create line string from points
 val lineString = LineString.fromLngLats(points)

 // Create polyline annotation
 val annotationApi = binding.mapView.annotations
 val polylineAnnotationManager = annotationApi.createPolylineAnnotationManager()

 val polylineOptions = PolylineAnnotationOptions()
 .withPoints(points)
 .withLineColor("#FF0000") // Red path
 .withLineWidth(5.0)

 polylineAnnotationManager.create(polylineOptions)

 // Add markers for start and end points
 val pointAnnotationManager = annotationApi.createPointAnnotationManager()

 // Start point marker
 val startPointOptions = PointAnnotationOptions()
 .withPoint(convertPositionToPoint(path.start))
 .withIconImage(requireContext().getDrawable(com.example.indoornavigation.R.drawable.ic_start_marker)!!)

 // End point marker
 val endPointOptions = PointAnnotationOptions()
 .withPoint(convertPositionToPoint(path.end))
 .withIconImage(requireContext().getDrawable(com.example.indoornavigation.R.drawable.ic_end_marker)!!)

 pointAnnotationManager.create(startPointOptions)
 pointAnnotationManager.create(endPointOptions)
 }

 private fun displayPathInfo(path: Path) {
 // Calculate total distance
 var totalDistance = 0.0
 for (i in 0 until path.waypoints.size - 1) {
 val p1 = path.waypoints[i]
 val p2 = path.waypoints[i + 1]

 val dx = p2.x - p1.x
 val dy = p2.y - p1.y

 totalDistance += Math.sqrt(dx * dx + dy * dy)
 }

 // Display path information
 binding.pathInfoText.text = "Distance: ${String.format("%.1f", totalDistance)} meters"

 // Floor changes
 val floorChanges = path.waypoints.zipWithNext { a, b -> a.floor != b.floor }.count { it }
 binding.floorChangesText.text = "Floor changes: $floorChanges"
 }

 private fun observePositionUpdates() {
 viewLifecycleOwner.lifecycleScope.launch {
 positioningViewModel.currentPosition.collectLatest { position ->
 position?.let {
 updateUserLocationOnMap(it)
 }
 }
 }
 }

 private fun updateUserLocationOnMap(position: Position) {
 // Convert app coordinates to map coordinates
 val point = convertPositionToPoint(position)

 // Create or update user location marker (similar to MapFragment)
 val annotationApi = binding.mapView.annotations
 val pointAnnotationManager = annotationApi.createPointAnnotationManager()

 val pointAnnotationOptions = PointAnnotationOptions()
 .withPoint(point)
 .withIconImage(requireContext().getDrawable(com.example.indoornavigation.R.drawable.ic_location_marker)!!)

 pointAnnotationManager.create(pointAnnotationOptions)
 }

 private fun convertPositionToPoint(position: Position): Point {
 // Convert app coordinates to map coordinates
 // This conversion will depend on how your floor plan is georeferenced
 return Point.fromLngLat(
 position.x / 1000.0, // Example conversion
 position.y / 1000.0 // Example conversion
 )
 }

 override fun onDestroyView() {
 super.onDestroyView()
 _binding = null
 }
}
