 private const val BLUETOOTH_REQUEST_CODE = 1002
 }

 override fun onCreate(savedInstanceState: Bundle?) {
 super.onCreate(savedInstanceState)

 // Initialize Mapbox with token
 MapboxOptions.accessToken = "pk.eyJ1IjoiYXppempvZSIsImEiOiJjbWFlMjhvbXQwMzU2MmxyMnMzcGU5bHk0In0.mwicRE2jWQFBJuTM2rC95Q"

