+package com.example.indoornavigation.utils

+import com.example.indoornavigation.data.models.Path
+import com.example.indoornavigation.data.models.Position
+import kotlin.math.pow
+import kotlin.math.sqrt
+
+/**
+ * Engine for finding paths between positions using A* algorithm
+ */
+class PathfindingEngine(private val nodes: List<Position>) {
+
+ /**
+ * Find a path between start and end positions
+ */
+ fun findPath(start: Position, end: Position): Path? {
+ // Convert positions to indices in our node list
+ val startNode = findClosestNode(start)
+ val endNode = findClosestNode(end)
+ 
+ // If we couldn't find close nodes, return null
+ if (startNode == null || endNode == null) return null
+ 
+ // Basic implementation - just create a direct path for now
+ // In a real app, this would use A* or other pathfinding algorithm
+ val waypoints = mutableListOf<Position>()
+ waypoints.add(start)
+ 
+ // If we need to change floors, go to the elevator on current floor first
+ if (start.floor != end.floor) {
+ // Find elevator node on start floor
+ val startElevator = nodes.find { it.x == 25.0 && it.y == 25.0 && it.floor == start.floor }
+ if (startElevator != null) {
+ waypoints.add(startElevator)
+ }
+ 
+ // Then go to elevator on end floor
+ val endElevator = nodes.find { it.x == 25.0 && it.y == 25.0 && it.floor == end.floor }
+ if (endElevator != null) {
+ waypoints.add(endElevator)
+ }
+ }
+ 
+ // Add midpoints on the same floor
+ val sameFloorNodes = nodes.filter { it.floor == end.floor }
+ 
+ // Find a few key waypoints to make the path look more natural
+ // In a real app, this would be calculated using proper pathfinding
+ if (sameFloorNodes.isNotEmpty()) {
+ // Add a node in the middle of the corridor
+ val corridorNode = sameFloorNodes.find { 
+ it.x >= 20 && it.x <= 30 && it.y >= 20 && it.y <= 30
+ }
+ corridorNode?.let { waypoints.add(it) }
+ }
+ 
+ waypoints.add(end)
+ 
+ return Path(start, end, waypoints)
+ }
+ 
+ /**
+ * Find the node closest to a given position
+ */
+ private fun findClosestNode(position: Position): Position? {
+ // Filter nodes on the same floor
+ val sameFloorNodes = nodes.filter { it.floor == position.floor }
+ if (sameFloorNodes.isEmpty()) return null
+ 
+ // Find closest node by Euclidean distance
+ return sameFloorNodes.minByOrNull { node ->
+ val dx = node.x - position.x
+ val dy = node.y - position.y
+ sqrt(dx.pow(2) + dy.pow(2))
+ }
+ }
+}
